selector {
	animation: flutuando01 15s ease;
	animation-iteration-count: infinite;
}
 
@keyframes flutuando01 {
	0% {
    	transform: translateY(0px) rotate(15deg);
	} 50% {
    	transform: translateY(-80px) rotate(-15deg);
	} 100% {
    	transform: translateY(0px) rotate(15deg);
	}
}

selector {
	animation: flutuando05 8s ease;
