}
 
@keyframes flutuando05 {
	0% {
    	transform: translateY(0px) rotate(15deg);
	} 50% {
    	transform: translateY(-80px) rotate(-15deg);
	} 100% {
    	transform: translateY(0px) rotate(15deg);
	}
}

