    background: rgb(255,236,201);
    background: linear-gradient(90deg, rgba(255,236,201,1) 0%, rgba(255,197,129,1) 100%);
}
/*FAIXA NUMERO 2*/
#text-wrapper2 {
    background: rgb(15,15,15);
background: linear-gradient(90deg, rgba(15,15,15,1) 0%, rgba(70,70,70,1) 99%);
}

