.text-wrapper h1{
    white-space: nowrap;
    font-family: "Sora";
    font-size: 12px;
    text-transform: uppercase;
    display: inline;
}


/*TEXTO DA FAIXA NUMERO 1*/
#text-wrapper1 h1 {
    color: #1a0e0b;
    animation: animafaixa 5s linear infinite;
}
/*TEXTO DA FAIXA NUMERO 2*/
